client/src/pages/new-debate.tsx.content:
import { useState } from "react";
import { useLocation } from "wouter";
import { useMutation } from "@tanstack/react-query";
import { ArrowLeft } from "lucide-react";
import { Link } from "wouter";
import { Button } from "@/components/ui/button";
import { Card } from "@/components/ui/card";
import { DebateForm } from "@/components/debate-form";
import { useToast } from "@/hooks/use-toast";
import { apiRequest } from "@/lib/queryClient";

export default function NewDebate() {
  const [, setLocation] = useLocation();
  const { toast } = useToast();

  const createDebateMutation = useMutation({
    mutationFn: async (data: any) => {
      const response = await apiRequest("POST", "/api/debates", data);
      return await response.json();
    },
    onSuccess: (data) => {
      toast({
        title: "Debate Created",
        description: "Your debate has been started successfully!",
      });
      setLocation(`/debate/${data.debateId}`);
    },
    onError: (error: Error) => {
      toast({
        title: "Error",
        description: error.message,
        variant: "destructive",
      });
    },
  });

  return (
    <div className="min-h-screen">
      <div className="max-w-2xl mx-auto px-4 py-8 space-y-8">
        <div className="flex items-center gap-4">
          <Link href="/">
            <Button variant="ghost" size="icon" data-testid="button-back">
              <ArrowLeft className="w-4 h-4" />
            </Button>
          </Link>
          <div>
            <h1 className="text-3xl font-bold">Start New Debate</h1>
            <p className="text-muted-foreground mt-1">
              Configure two AI personas and watch them debate
            </p>
          </div>
        </div>

        <Card className="p-8">
          <DebateForm
            onSubmit={(data) => createDebateMutation.mutate(data)}
            isSubmitting={createDebateMutation.isPending}
          />
        </Card>
      </div>
    </div>
  );
}

client/src/pages/home.tsx.content:
import { useQuery } from "@tanstack/react-query";
import { Link } from "wouter";
import { Plus, Sparkles } from "lucide-react";
import { Button } from "@/components/ui/button";
import { DebateCard } from "@/components/debate-card";
import { Skeleton } from "@/components/ui/skeleton";
import type { Debate, Persona } from "@shared/schema";

export default function Home() {
  const { data: debates, isLoading } = useQuery<
    (Debate & {
      personaA: Persona;
      personaB: Persona;
      argumentCount: number;
      spectatorCount: number;
    })[]
  >({
    queryKey: ["/api/debates"],
  });

  return (
    <div className="min-h-screen">
      <div className="max-w-7xl mx-auto px-4 py-8 space-y-8">
        <div className="space-y-4">
          <div className="flex items-center justify-between gap-4 flex-wrap">
            <div>
              <h1 className="text-4xl font-bold flex items-center gap-3">
                <Sparkles className="w-8 h-8 text-primary" />
                Agora Sim
              </h1>
              <p className="text-muted-foreground mt-2">
                Watch AI personas engage in structured debates
              </p>
            </div>
            <Link href="/new-debate">
              <Button size="lg" data-testid="button-new-debate">
                <Plus className="w-4 h-4 mr-2" />
                Start New Debate
              </Button>
            </Link>
          </div>
        </div>

        <div>
          <h2 className="text-2xl font-bold mb-6">Active Debates</h2>
          {isLoading ? (
            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
              {[1, 2, 3, 4, 5, 6].map((i) => (
                <Skeleton key={i} className="h-64" />
              ))}
            </div>
          ) : debates && debates.length > 0 ? (
            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
              {debates.map((debate) => (
                <DebateCard key={debate.id} debate={debate} />
              ))}
            </div>
          ) : (
            <div className="text-center py-16">
              <p className="text-muted-foreground mb-4">
                No active debates yet. Start one!
              </p>
              <Link href="/new-debate">
                <Button data-testid="button-start-first-debate">
                  <Plus className="w-4 h-4 mr-2" />
                  Create First Debate
                </Button>
              </Link>
            </div>
          )}
        </div>
      </div>
    </div>
  );
}

